"use strict";function show(){$.ajax({url:"../interface/showlist.php",type:"post",success:function(e){console.log(e);var t=JSON.parse(e),n=template("shoppingCat",{json:t.data});$(".list-title-2").append(n),$(".add").on("click",function(e){$(".num").html(Number($(".num").html())+1),$(".sum").html(Number($(".num").html())*Number($(".dj span").html())),$.get("../interface/updatewq.php",{id:1,type:"add"},function(e){JSON.parse(e)})});$(".reduce").on("click",function(e){$(".num").html(Number($(".num").html())-1),$(".sum").html(Number($(".num").html())*Number($(".dj span").html())),$.get("../interface/updatewq.php?id=1",{type:"reduce"},function(e){JSON.parse(e)})}),$(".dele").click(function(){$.get("../interface/delwq.php",{id:1},function(e){1==JSON.parse(e).code&&alert("商品删除成功")})})}})}show();